import i18n from "i18next";
import { initReactI18next } from "react-i18next";
import LanguageDetector from "i18next-browser-languagedetector";
import Backend from "i18next-http-backend";

const languages = [
  { code: "en", name: "English", nativeName: "English" },
  { code: "ja", name: "Japanese", nativeName: "日本語" },
  { code: "zh", name: "Chinese (Mandarin)", nativeName: "中文" },
  { code: "hi", name: "Hindi", nativeName: "हिन्दी" },
  { code: "es", name: "Spanish", nativeName: "Español" },
  { code: "ar", name: "Arabic", nativeName: "العربية" },
  { code: "fr", name: "French", nativeName: "Français" },
  { code: "pt", name: "Portuguese", nativeName: "Português" },
  { code: "ru", name: "Russian", nativeName: "Русский" },
  { code: "sw", name: "Swahili", nativeName: "Kiswahili" },
  { code: "ha", name: "Hausa", nativeName: "Hausa" },
];

// Available language codes for quick lookup
const availableLanguages = languages.map((lang) => lang.code);

// Enhanced language detection function
export const detectLanguage = (): string => {
  // Check if there's a manually selected language in localStorage (highest priority)
  const storedLanguage = localStorage.getItem("i18nextLng");
  if (
    storedLanguage &&
    storedLanguage !== "undefined" &&
    availableLanguages.includes(storedLanguage)
  ) {
    return storedLanguage;
  }

  // Check browser language preferences (navigator.languages)
  if (typeof navigator !== "undefined" && navigator.languages) {
    for (const browserLang of navigator.languages) {
      // Extract language code (e.g., 'en-US' -> 'en')
      const langCode = browserLang.split("-")[0];
      if (availableLanguages.includes(langCode)) {
        return langCode;
      }
    }
  }

  // Fallback to primary language if available
  if (typeof navigator !== "undefined" && navigator.language) {
    const langCode = navigator.language.split("-")[0];
    if (availableLanguages.includes(langCode)) {
      return langCode;
    }
  }

  // Final fallback to English
  return "en";
};

const initConfig = {
  fallbackLng: false as const, // Disable fallback to English - show translation keys instead
  lng: detectLanguage(), // Use our custom detection logic
  debug: import.meta.env.DEV, // Console logging only in development

  // Supported languages - all available languages
  supportedLngs: ["en", "hi", "zh", "es", "ar", "fr", "pt", "ru", "sw", "ha", "ja"],

  // Language detection options - prioritize browser language detection
  detection: {
    order: ["localStorage", "navigator", "htmlTag"],
    caches: ["localStorage"],
    lookupLocalStorage: "i18nextLng",
    checkWhitelist: true, // Only use supported languages
    excludeCacheFor: ["cimode"], // Don't cache dev mode
  },

  // Backend configuration for loading translations
  backend: {
    loadPath: `/locales/{{lng}}/{{ns}}.json`,
    addPath: "/locales/add/{{lng}}/{{ns}}",
    cache: true, // Enable caching for better performance
    allowMultiLoading: true, // Allow loading multiple namespaces at once
    reloadInterval: false,
  },

  // Interpolation options
  interpolation: {
    escapeValue: false, // React already handles XSS
    formatSeparator: ",",
  },

  // Namespace configuration
  ns: [
    "common",
    "navigation",
    "pages",
    "services",
    "products",
    "forms",
    "data",
    "products-data",
    "chatbot",
    "acronyms",
  ],
  defaultNS: "common",

  // React i18next options
  react: {
    bindI18n: "languageChanged",
    bindI18nStore: "",
    transEmptyNodeValue: "",
    transSupportBasicHtmlNodes: true,
    transKeepBasicHtmlNodesFor: ["br", "strong", "i", "em"],
    useSuspense: false,
  },

  // Return translation key when missing - never fallback to English
  returnEmptyString: false,
  returnNull: false,
  returnObjects: false,
  saveMissing: false,

  // Return missing keys with namespace prefix for visibility
  appendNamespaceToMissingKey: true,

  // Resources loading strategy
  partialBundledLanguages: true,
};

i18n.use(Backend).use(LanguageDetector).use(initReactI18next).init(initConfig);

// RTL language configuration
const rtlLanguages = ["ar"];

// Function to check if a language is RTL
export const isRTL = (language: string): boolean => {
  return rtlLanguages.includes(language);
};

// Function to update document direction based on language
export const updateDocumentDirection = (language: string) => {
  const isRtl = isRTL(language);
  document.documentElement.dir = isRtl ? "rtl" : "ltr";
  document.documentElement.lang = language;
};

// Set up language change handler
i18n.on("languageChanged", (lng) => {
  updateDocumentDirection(lng);
});

// Set up missing key logging for development
i18n.on("missingKey", (lngs, namespace, key) => {
  if (import.meta.env.DEV) {
    console.error(
      `🚨 MISSING TRANSLATION: ${namespace}:${key} for languages: ${lngs.join(
        ", "
      )}`
    );
  }
});

// Set initial document direction when i18n is ready
i18n.on("initialized", () => {
  updateDocumentDirection(i18n.language);
});

// Ensure direction is set immediately if already initialized
if (i18n.isInitialized) {
  updateDocumentDirection(i18n.language);
}

export { languages };
export default i18n;

client/src/components/ui/search-dialog.t.content:
import { useState, useEffect, useMemo } from "react";
import { useTranslation } from "react-i18next";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Search, X, Package, Layers, FolderOpen, Clock } from "lucide-react";
import { searchAll, SearchResult } from "@/lib/searchUtils";
import { Link } from "wouter";
import { getProductImageSrc } from "@/assets/images";
import { getProducts } from "@/data/constants";

const RECENT_SEARCHES_KEY = "recent_searches";
const MAX_RECENT_SEARCHES = 8;

interface SearchDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
}

// Helper functions for recent searches
const getRecentSearches = (): string[] => {
  try {
    const stored = localStorage.getItem(RECENT_SEARCHES_KEY);
    return stored ? JSON.parse(stored) : [];
  } catch {
    return [];
  }
};

const saveRecentSearch = (searchTerm: string) => {
  if (!searchTerm.trim() || searchTerm.trim().length < 2) return;

  try {
    const recent = getRecentSearches();
    const filtered = recent.filter(
      (term) => term.toLowerCase() !== searchTerm.toLowerCase(),
    );
    const updated = [searchTerm, ...filtered].slice(0, MAX_RECENT_SEARCHES);
    localStorage.setItem(RECENT_SEARCHES_KEY, JSON.stringify(updated));
  } catch {
    // Silently fail if localStorage is not available
  }
};

export function SearchDialog({ open, onOpenChange }: SearchDialogProps) {
  const { t } = useTranslation(["common", "products"]);
  const [query, setQuery] = useState("");
  const [category, setCategory] = useState<string>("all");
  const [sortBy, setSortBy] = useState<"relevance" | "name-az" | "name-za">(
    "relevance",
  );
  const [debouncedQuery, setDebouncedQuery] = useState("");
  const [recentSearches, setRecentSearches] = useState<string[]>([]);

  // Load recent searches on mount and when dialog opens
  useEffect(() => {
    if (open) {
      setRecentSearches(getRecentSearches());
    }
  }, [open]);

  // Debounce search query
  useEffect(() => {
    const timer = setTimeout(() => {
      setDebouncedQuery(query);
    }, 300);

    return () => clearTimeout(timer);
  }, [query]);

  // Save search to recent when user performs a search
  useEffect(() => {
    if (debouncedQuery.trim().length >= 2) {
      saveRecentSearch(debouncedQuery.trim());
      setRecentSearches(getRecentSearches());
    }
  }, [debouncedQuery]);

  // Get search results
  const searchResults = useMemo(() => {
    if (debouncedQuery.trim().length < 2) {
      return [];
    }

    return searchAll(
      {
        query: debouncedQuery,
        category: category === "all" ? undefined : category,
        sortBy,
        limit: 50,
      },
      t,
    );
  }, [debouncedQuery, category, sortBy, t]);

  // Get categories for filter
  const categories = getProducts(t);

  // Reset on close
  useEffect(() => {
    if (!open) {
      setQuery("");
      setCategory("all");
      setSortBy("relevance");
    }
  }, [open]);

  const handleResultClick = () => {
    onOpenChange(false);
  };

  const handleRecentSearchClick = (searchTerm: string) => {
    setQuery(searchTerm);
  };

  const getResultIcon = (type: SearchResult["type"]) => {
    switch (type) {
      case "product":
        return <Package className="h-4 w-4" />;
      case "productGroup":
        return <Layers className="h-4 w-4" />;
      case "subcategory":
        return <FolderOpen className="h-4 w-4" />;
    }
  };

  const getResultTypeLabel = (type: SearchResult["type"]) => {
    switch (type) {
      case "product":
        return t("common:search.products");
      case "productGroup":
        return t("common:search.productGroups");
      case "subcategory":
        return t("common:search.subcategories");
    }
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-3xl max-h-[90vh] p-0 flex flex-col">
        <DialogHeader className="px-6 pt-6 pb-4 border-b flex-shrink-0">
          <DialogTitle className="text-2xl">
            {t("common:search.searchProducts")}
          </DialogTitle>
        </DialogHeader>

        <div className="px-6 py-4 space-y-4 flex-shrink-0">
          {/* Search Input */}
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-muted-foreground" />
            <Input
              data-testid="input-search"
              type="text"
              placeholder={t("common:search.placeholder")}
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              className="pl-10 pr-10 h-12 text-lg"
              autoFocus
            />
            {query && (
              <button
                data-testid="button-clear-search"
                onClick={() => setQuery("")}
                className="absolute right-3 top-1/2 transform -translate-y-1/2 text-muted-foreground hover:text-foreground"
              >
                <X className="h-5 w-5" />
              </button>
            )}
          </div>

          {/* Filters */}
          <div className="flex gap-3 flex-wrap">
            <Select value={category} onValueChange={setCategory}>
              <SelectTrigger
                data-testid="select-category-filter"
                className="w-[200px]"
              >
                <SelectValue
                  placeholder={t("common:search.filterByCategory")}
                />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">
                  {t("common:search.allCategories")}
                </SelectItem>
                {categories.map((cat) => (
                  <SelectItem key={cat.id} value={cat.id}>
                    {cat.title}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>

            <Select
              value={sortBy}
              onValueChange={(val) => setSortBy(val as any)}
            >
              <SelectTrigger data-testid="select-sort-by" className="w-[180px]">
                <SelectValue placeholder={t("common:search.sortBy")} />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="relevance">
                  {t("common:search.relevance")}
                </SelectItem>
                <SelectItem value="name-az">
                  {t("common:search.nameAZ")}
                </SelectItem>
                <SelectItem value="name-za">
                  {t("common:search.nameZA")}
                </SelectItem>
              </SelectContent>
            </Select>
          </div>

          {/* Recent Searches (shown when no query and there are recent searches) */}
          {query.trim().length < 2 && recentSearches.length > 0 && (
            <div className="space-y-3">
              <div className="flex items-center gap-2">
                <Clock className="h-4 w-4 text-muted-foreground" />
                <p className="text-sm font-medium text-muted-foreground">
                  {t("common:search.recentSearches")}:
                </p>
              </div>
              <div className="flex flex-wrap gap-2">
                {recentSearches.map((recentSearch, index) => (
                  <button
                    key={`${recentSearch}-${index}`}
                    onClick={() => handleRecentSearchClick(recentSearch)}
                    data-testid={`recent-search-${recentSearch.toLowerCase().replace(/\s+/g, "-")}`}
                    className="group relative px-3 py-1.5 rounded-full text-xs font-medium transition-all duration-200 
                      bg-muted/60 hover:bg-primary/10 
                      border border-border/50 hover:border-primary/40
                      text-foreground/70 hover:text-secondary
                      hover:shadow-sm hover:scale-[1.02] active:scale-95"
                  >
                    <span className="relative z-10">{recentSearch}</span>
                  </button>
                ))}
              </div>
            </div>
          )}
        </div>

        {/* Results */}
        {debouncedQuery.trim().length >= 2 && (
          <div className="px-6 pb-6 flex-1 flex flex-col min-h-0">
            <div className="mb-3 flex-shrink-0">
              <p
                className="text-sm text-muted-foreground"
                data-testid="text-results-count"
              >
                {searchResults.length > 0
                  ? t("common:search.showingResults", {
                      count: searchResults.length,
                    })
                  : t("common:search.noResultsFound")}
              </p>
            </div>

            <div className="flex-1 pr-4 overflow-y-auto min-h-0">
              {searchResults.length === 0 ? (
                <div className="text-center py-12">
                  <p className="text-muted-foreground">
                    {t("common:search.noResultsMessage")}
                  </p>
                </div>
              ) : (
                <div className="space-y-3">
                  {searchResults.map((result) => (
                    <Link
                      key={`${result.type}-${result.id}`}
                      href={result.url}
                      onClick={handleResultClick}
                    >
                      <div
                        data-testid={`search-result-${result.type}-${result.id}`}
                        className="flex gap-4 p-4 rounded-lg border bg-card hover:bg-accent hover:shadow-md transition-all cursor-pointer group"
                      >
                        {/* Image */}
                        {result.image && (
                          <div className="flex-shrink-0 w-20 h-20 rounded-md overflow-hidden bg-muted">
                            <img
                              src={getProductImageSrc(result.image) || ""}
                              alt={result.title}
                              className="w-full h-full object-cover group-hover:scale-105 transition-transform"
                            />
                          </div>
                        )}

                        {/* Content */}
                        <div className="flex-1 min-w-0">
                          <div className="flex items-start gap-2 mb-1">
                            <Badge
                              variant="outline"
                              className="flex items-center gap-1 text-xs"
                            >
                              {getResultIcon(result.type)}
                              {getResultTypeLabel(result.type)}
                            </Badge>
                            {result.manufacturer && (
                              <Badge variant="secondary" className="text-xs">
                                {result.manufacturer}
                              </Badge>
                            )}
                          </div>
                          <h3 className="font-semibold text-base mb-1 line-clamp-1 group-hover:text-primary transition-colors">
                            {result.title}
                          </h3>
                          <p className="text-sm text-muted-foreground line-clamp-2">
                            {result.description}
                          </p>
                        </div>
                      </div>
                    </Link>
                  ))}
                </div>
              )}
            </div>
          </div>
        )}
      </DialogContent>
    </Dialog>
  );
}

client/src/components/forms/frontend-quo.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { useTranslation } from "react-i18next";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Checkbox } from "@/components/ui/checkbox";
import { useToast } from "@/hooks/use-toast";
import { Loader2, FileText, Mail, Phone } from "lucide-react";
import { FRONTEND_CONFIG } from "@/lib/frontend-config";
import { getServiceOptions } from "@/data/constants";

// Frontend-only quote form schema - validation messages will be handled by translation keys
// eslint-disable-next-line @typescript-eslint/no-unused-vars
const createQuoteFormSchema = (t: (translationKey: string) => string) =>
  z.object({
    // Contact Information
    name: z.string().min(2, t("forms:quote.validation.nameMin")),
    email: z.string().email(t("forms:quote.validation.emailInvalid")),
    phone: z.string().min(10, t("forms:quote.validation.phoneMin")),
    company: z.string().min(1, t("forms:quote.validation.companyRequired")),
    designation: z.string().optional(),

    // Project Information
    projectTitle: z
      .string()
      .min(1, t("forms:quote.validation.projectTitleRequired")),
    projectType: z
      .string()
      .min(1, t("forms:quote.validation.projectTypeRequired")),
    budget: z.string().optional(),
    timeline: z.string().min(1, t("forms:quote.validation.timelineRequired")),

    // Technical Requirements
    services: z
      .array(z.string())
      .min(1, t("forms:quote.validation.servicesRequired")),
    specifications: z
      .string()
      .min(20, t("forms:quote.validation.specificationsMin")),
    additionalRequirements: z.string().optional(),

    // Project Details
    industryType: z.string().optional(),
    projectScale: z.string().optional(),
    preferredBrands: z.string().optional(),
  });

type QuoteFormData = z.infer<ReturnType<typeof createQuoteFormSchema>>;

// Service options will be populated inside the component where t is available

export default function FrontendQuoteForm() {
  const [isSubmitting, setIsSubmitting] = useState(false);
  const { toast } = useToast();
  const { t } = useTranslation("forms");

  const quoteFormSchema = createQuoteFormSchema(t);

  // Get translated service options
  const serviceOptions = getServiceOptions(t).map((service) => ({
    id: service.value,
    label: service.label,
  }));

  const form = useForm<QuoteFormData>({
    resolver: zodResolver(quoteFormSchema),
    defaultValues: {
      name: "",
      email: "",
      phone: "",
      company: "",
      designation: "",
      projectTitle: "",
      projectType: "",
      budget: "",
      timeline: "",
      services: [],
      specifications: "",
      additionalRequirements: "",
      industryType: "",
      projectScale: "",
      preferredBrands: "",
    },
  });

  const onSubmit = async (data: QuoteFormData) => {
    setIsSubmitting(true);

    try {
      // Create FormData object for PHP
      const formData = new FormData();

      // Contact Information
      formData.append("name", data.name);
      formData.append("email", data.email);
      formData.append("phone", data.phone);
      formData.append("company", data.company);
      formData.append("designation", data.designation || "");

      // Project Information
      formData.append("projectTitle", data.projectTitle);
      formData.append("projectType", data.projectType);
      formData.append("budget", data.budget || "");
      formData.append("timeline", data.timeline);
      formData.append("industryType", data.industryType || "");
      formData.append("projectScale", data.projectScale || "");

      // Services (send as comma-separated string)
      formData.append("services", data.services.join(", "));

      // Technical Requirements
      formData.append("specifications", data.specifications);
      formData.append(
        "additionalRequirements",
        data.additionalRequirements || ""
      );
      formData.append("preferredBrands", data.preferredBrands || "");

      // Send to PHP handler
      const response = await fetch("/quote-handler.php", {
        method: "POST",
        body: formData,
      });

      const result = await response.json();

      if (result.success) {
        toast({
          title: t("forms:quote.success.title"),
          description: t("forms:quote.success.description"),
          duration: 6000,
        });
        form.reset();
      } else {
        throw new Error(result.message);
      }
    } catch (error) {
      console.error("Quote form error:", error);
      toast({
        title: t("forms:quote.error.title"),
        description: t("forms:quote.error.description", {
          phone: FRONTEND_CONFIG.company.phone,
          email: FRONTEND_CONFIG.company.email,
        }),
        duration: 6000,
      });
    }

    setIsSubmitting(false);
  };

  return (
    <Card className="text-foreground">
      <CardContent className="p-6">
        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
            {/* Contact Information Section */}
            <div className="space-y-4">
              <h3 className="text-lg font-semibold text-foreground border-b border-border pb-2 text-wrap-safe responsive-text">
