              <SheetDescription className="sr-only">
                {t("navigation:mobile.menuDescription")}
              </SheetDescription>
              <AnimatePresence>
                <motion.nav
                  className="flex flex-col space-y-4 mt-8 pb-8"
                  initial="closed"
                  animate="open"
                  exit="closed"
                  variants={{
                    open: {
                      transition: { staggerChildren: 0.1, delayChildren: 0.2 },
