              {t("common:buttons.backTo")} {product.parentCategoryTitle}
            </Link>
          </Button>
        </motion.div>
      </div>
    );
  }

  const IconComponent = product.icon;

  // Get product groups for this subcategory
  const productGroups = getGroupsForSubcategory(slug);

  return (
    <>
      <SEO
        title={product.seo.title}
