                productGroups.map((group, index) => {
                  // Use the title and description from the ProductGroup
                  const groupTitle =
                    group.title || group.key.toUpperCase().replace("-", " ");
                  const groupDescription =
                    group.description ||
                    t("products-data:defaults.groupDescription");

                  return (
                    <Link
                      key={group.key}
