  h3,
  h4,
  h5,
  h6 {
    @apply font-semibold;
  }
}

@layer components {
  .gradient-bg {
    background: linear-gradient(135deg, var(--primary) 0%, var(--accent) 100%);
  }

  .gradient-text {
    background: linear-gradient(
      135deg,
      var(--primary) 0%,
      var(--secondary) 100%
    );
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  /* Custom button styles */
  .btn-secondary {
    @apply bg-secondary text-white hover:bg-secondary hover:opacity-90 transition-all duration-300;
  }

  .btn-outline {
    @apply border-2 border-border text-white bg-transparent hover:bg-background hover:text-foreground transition-all duration-300;
  }

  .text-responsive-xs {
    @apply text-xs sm:text-sm;
  }

  .text-responsive-sm {
    @apply text-sm sm:text-base md:text-lg;
  }

  .text-responsive-base {
    @apply text-base sm:text-lg md:text-xl;
  }

  .text-responsive-lg {
    @apply text-lg sm:text-xl md:text-2xl lg:text-3xl;
  }

  .text-responsive-xl {
    @apply text-xl sm:text-2xl md:text-3xl lg:text-4xl xl:text-5xl;
  }

  .text-responsive-2xl {
    @apply text-2xl sm:text-3xl md:text-4xl lg:text-5xl xl:text-6xl;
  }

  .btn-responsive {
    @apply px-3 py-2 sm:px-4 sm:py-2 md:px-6 md:py-3 text-sm sm:text-base;
  }

  .mobile-full {
    @apply w-full sm:w-auto;
  }

  .hero-overlay {
    background: linear-gradient(
      135deg,
      hsl(var(--primary) / 0.9) 0%,
      hsl(var(--primary) / 0.8) 100%
    );
  }

  .btn-primary {
    @apply bg-primary text-primary-foreground hover:opacity-90 px-6 py-3 rounded-lg font-semibold transition-all;
  }

  .btn-secondary {
    @apply bg-secondary text-secondary-foreground hover:opacity-90 px-6 py-3 rounded-lg font-semibold transition-all;
  }

  .btn-outline {
    @apply border-2 border-primary text-primary hover:bg-primary hover:text-primary-foreground px-6 py-3 rounded-lg font-semibold transition-all;
  }
}

