import { useState, useEffect, useRef } from "react";
import { motion } from "framer-motion";
import { Phone, MessageCircle, ArrowRight, ArrowLeft, ChevronRight, ChevronLeft } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { getNewestProducts, shouldShowPopup } from "@/data/productImages";

interface StockAlertSidebarProps {
  isOpen: boolean;
  onToggle: () => void;
  headerHeight?: number;
}

export function StockAlertSidebar({ isOpen, onToggle, headerHeight = 0 }: StockAlertSidebarProps) {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [footerOffset, setFooterOffset] = useState(0);
  const sidebarRef = useRef<HTMLElement>(null);

  const stockProducts = getNewestProducts(6);
  const showSidebar = shouldShowPopup();

  // Detect footer visibility and adjust sidebar position
  useEffect(() => {
    const handleScroll = () => {
      const footer = document.querySelector('footer');
      if (!footer || !sidebarRef.current) return;

      const footerRect = footer.getBoundingClientRect();
      const viewportHeight = window.innerHeight;

      // If footer is visible in viewport
      if (footerRect.top < viewportHeight) {
        // Calculate how much the footer is overlapping with where the sidebar should be
        const overlap = viewportHeight - footerRect.top;
        setFooterOffset(overlap);
      } else {
        setFooterOffset(0);
      }
    };

    window.addEventListener('scroll', handleScroll);
    window.addEventListener('resize', handleScroll);

    // Initial check
    handleScroll();

    return () => {
      window.removeEventListener('scroll', handleScroll);
      window.removeEventListener('resize', handleScroll);
    };
  }, []);

  useEffect(() => {
    if (!showSidebar || stockProducts.length === 0 || !isOpen) return;

    const autoSlideInterval = setInterval(() => {
      setCurrentIndex((prev) => (prev + 1) % stockProducts.length);
    }, 3000);

    return () => clearInterval(autoSlideInterval);
  }, [showSidebar, stockProducts.length, isOpen]);

  const nextProduct = () => {
    setCurrentIndex((prev) => (prev + 1) % stockProducts.length);
  };

  const prevProduct = () => {
    setCurrentIndex(
      (prev) => (prev - 1 + stockProducts.length) % stockProducts.length,
    );
  };

  const handleCall = () => {
    window.location.href = "tel:+919462771662";
  };

  const handleWhatsApp = () => {
    window.open(
      "https://wa.me/919462771662?text=Hi, I'm interested in the ready stock products",
      "_blank",
    );
  };

  if (!showSidebar || stockProducts.length === 0) return null;

  return (
    <aside
      className="w-80 lg:w-96 bg-background border-r-4 border-primary shadow-xl flex-shrink-0 max-h-screen overflow-y-auto"
      data-testid="aside-stock-alert"
    >
      <div className="p-4 space-y-4">
        <div className="text-center">
          <motion.div
            animate={{ scale: [1, 1.05, 1] }}
            transition={{ duration: 2, repeat: Infinity }}
            className="inline-block"
          >
            <Badge className="bg-red-600 text-white text-sm px-3 py-1.5 mb-2">
              ⚡ READY STOCK
            </Badge>
          </motion.div>

          <h3 className="text-xl font-bold text-foreground mb-1">
            Premium Products
          </h3>
          <p className="text-sm text-muted-foreground mb-1">
            High-quality instrumentation
          </p>
          <p className="text-sm font-semibold text-destructive">
            🔥 Order now!
          </p>
        </div>

        <div className="relative">
          <div className="flex items-center justify-between gap-2 mb-2">
            <Button
              variant="outline"
              size="icon"
              onClick={prevProduct}
              className="h-8 w-8 shrink-0"
              data-testid="button-prev-product"
            >
              <ArrowLeft className="h-3 w-3" />
            </Button>

            <div className="flex-1">
              <motion.div
                key={currentIndex}
                initial={{ opacity: 0, x: 20 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: -20 }}
                transition={{ duration: 0.3 }}
                className="relative group"
              >
                <div className="bg-white dark:bg-gray-900 rounded-lg p-2 border-2 border-border hover:border-primary transition-colors">
                  <div className="aspect-square overflow-hidden rounded-md mb-2">
                    <img
                      src={stockProducts[currentIndex].image}
                      alt={stockProducts[currentIndex].title}
                      className="w-full h-full object-contain group-hover:scale-110 transition-transform duration-300"
                      data-testid={`img-stock-product-${stockProducts[currentIndex].id}`}
                    />
                  </div>
                  <h4 className="text-sm font-semibold text-foreground text-center line-clamp-2">
                    {stockProducts[currentIndex].title}
                  </h4>
                </div>
              </motion.div>
            </div>

            <Button
              variant="outline"
              size="icon"
              onClick={nextProduct}
              className="h-8 w-8 shrink-0"
              data-testid="button-next-product"
            >
              <ArrowRight className="h-3 w-3" />
            </Button>
          </div>
        </div>

        <div className="bg-gradient-to-r from-primary/10 to-secondary/10 rounded-lg p-3 space-y-2">
          <motion.div whileHover={{ scale: 1.02 }} whileTap={{ scale: 0.98 }}>
            <Button
              onClick={handleCall}
              className="w-full bg-blue-600 hover:bg-blue-700 text-white py-6 shadow-md"
              data-testid="button-call-now"
            >
              <Phone className="mr-2 h-4 w-4" />
              <div className="flex flex-col items-start">
                <span className="text-xs font-normal">Call Now</span>
                <span className="text-base font-bold">+91-94627-71662</span>
              </div>
            </Button>
          </motion.div>

          <motion.div whileHover={{ scale: 1.02 }} whileTap={{ scale: 0.98 }}>
            <Button
              onClick={handleWhatsApp}
              className="w-full bg-green-600 hover:bg-green-700 text-white py-6 shadow-md"
              data-testid="button-whatsapp-now"
            >
              <MessageCircle className="mr-2 h-4 w-4" />
              <div className="flex flex-col items-start">
                <span className="text-xs font-normal">WhatsApp Now</span>
                <span className="text-base font-bold">+91-94627-71662</span>
              </div>
            </Button>
          </motion.div>
        </div>

        <div className="text-center">
          <p className="text-xs text-muted-foreground leading-tight">
            🚚 Same-day dispatch | ✅ ISO certified | 💯 Genuine quality
          </p>
        </div>
      </div>
    </aside>
  );
}

client/src/App.tsx.content:
import { Switch, Route, useLocation } from "wouter";
import { lazy, Suspense, useEffect } from "react";
import { useTranslation } from "react-i18next";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import { HelmetProvider } from "react-helmet-async";
import { ThemeProvider } from "@/hooks/use-theme";

// Layout
import Layout from "@/components/layout/layout";

// Pages
import Home from "@/pages/home";
import About from "@/pages/about";
import Services from "@/pages/services";
import Products from "@/pages/products";
import Projects from "@/pages/projects";
import Gallery from "@/pages/gallery";
import Career from "@/pages/career";
import Contact from "@/pages/contact";
import Quote from "@/pages/quote";
import Sitemap from "@/pages/sitemap";
import NotFound from "@/pages/not-found";

// Lazy load dynamic service category page
const ServiceCategoryPage = lazy(
  () => import("@/pages/service-category-dynamic")
);

// Lazy load dynamic product category page
const ProductCategoryDynamic = lazy(
  () => import("@/pages/product-category-dynamic")
);

// Lazy load dynamic product sub-category page
const ProductSubCategoryDynamic = lazy(
  () => import("@/pages/products-sub-category-dynamic")
);

// Lazy load dynamic product group page
const ProductGroupDynamic = lazy(() => import("@/pages/product-group-dynamic"));

// Lazy load dynamic product detail page
const ProductDetailDynamic = lazy(
  () => import("@/pages/product-detail-dynamic")
);

// Loading component for Suspense fallback
const LoadingSpinner = () => {
  return (
    <div className="min-h-screen flex items-center justify-center bg-background">
      <div className="text-center">
        <div className="w-8 h-8 border-4 border-primary border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
        <p className="text-muted-foreground">Loading...</p>
      </div>
    </div>
  );
};

function Router() {
  const [location] = useLocation();

  useEffect(() => {
    window.scrollTo({ top: 0, left: 0, behavior: "smooth" });
  }, [location]);

  return (
    <Layout>
      <Suspense fallback={<LoadingSpinner />}>
        <Switch>
          <Route path="/" component={Home} />
          <Route path="/about" component={About} />
          <Route path="/services" component={Services} />
          <Route path="/products" component={Products} />

          {/* Dynamic Service Category Pages */}
          <Route
            path="/services-category/:slug"
            component={ServiceCategoryPage}
          />

          {/* Product Detail with Group Context Route (most specific first) */}
          <Route
            path="/products/:parentSlug/:subcategorySlug/:groupSlug/:slug"
            component={ProductDetailDynamic}
          />

          {/* Dynamic Product Group Route (hierarchical) - 3 segments */}
          <Route
            path="/products/:parentSlug/:subcategorySlug/:groupSlug"
            component={ProductGroupDynamic}
          />

          {/* Dynamic Product Sub-Category Route (hierarchical) - 2 segments */}
          <Route
            path="/products/:parentSlug/:slug"
            component={ProductSubCategoryDynamic}
          />

          {/* Dynamic Product Category Route (catches single-slug patterns) */}
          <Route path="/products/:slug" component={ProductCategoryDynamic} />

          <Route path="/projects" component={Projects} />
          <Route path="/gallery" component={Gallery} />
          <Route path="/career" component={Career} />
          <Route path="/contact" component={Contact} />
          <Route path="/quote" component={Quote} />

          <Route path="/sitemap" component={Sitemap} />
          <Route component={NotFound} />
        </Switch>
      </Suspense>
    </Layout>
  );
}

function App() {
  const { ready } = useTranslation();

  if (!ready) {
    return <LoadingSpinner />;
  }

  return (
    <HelmetProvider>
      <ThemeProvider defaultTheme="system" storageKey="powerton-ui-theme">
        <TooltipProvider>
          <Toaster />
          <Router />
        </TooltipProvider>
      </ThemeProvider>
    </HelmetProvider>
  );
}

export default App;

