import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { useTranslation } from "react-i18next";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { Loader2, Send, Mail, Phone } from "lucide-react";
import { FRONTEND_CONFIG } from "@/lib/frontend-config";
import { getServiceOptions } from "@/data/constants";

// Create schema function that uses translations
const createContactFormSchema = (t: any) =>
  z.object({
    name: z.string().min(2, t("contact.fields.name.error")),
    email: z.string().email(t("contact.fields.email.error")),
    phone: z.string().min(10, t("contact.fields.phone.error")),
    company: z.string().optional(),
    subject: z.string().min(1, t("contact.fields.subject.error")),
    message: z.string().min(10, t("contact.fields.message.error")),
    serviceType: z.string().optional(),
  });

type ContactFormData = {
  name: string;
  email: string;
  phone: string;
  company?: string;
  subject: string;
  message: string;
  serviceType?: string;
};

export default function FrontendContactForm() {
  const [isSubmitting, setIsSubmitting] = useState(false);
  const { toast } = useToast();
  const { t } = useTranslation("forms");

  const contactFormSchema = createContactFormSchema(t);

  const form = useForm<ContactFormData>({
    resolver: zodResolver(contactFormSchema),
    defaultValues: {
      name: "",
      email: "",
      phone: "",
      company: "",
      subject: "",
      message: "",
      serviceType: "",
    },
  });

  const onSubmit = async (data: ContactFormData) => {
    setIsSubmitting(true);

    try {
      // Create FormData object for PHP
      const formData = new FormData();
      formData.append("name", data.name);
      formData.append("email", data.email);
      formData.append("phone", data.phone);
      formData.append("company", data.company || "");
      formData.append("subject", data.subject);
      formData.append("message", data.message);
      formData.append("serviceType", data.serviceType || "");

      // Send to PHP handler
      const response = await fetch("/contact-handler.php", {
        method: "POST",
        body: formData,
      });

      const result = await response.json();

      if (result.success) {
        toast({
          title: t("contact.messages.success"),
          description: t("contact.messages.successDesc"),
          duration: 5000,
        });
        form.reset();
      } else {
        throw new Error(result.message);
      }
    } catch (error) {
      console.error("Contact form error:", error);
      toast({
        title: t("contact.messages.error"),
        description: t("contact.messages.errorDesc", {
          phone: FRONTEND_CONFIG.company.phone,
          email: FRONTEND_CONFIG.company.email,
        }),
        duration: 6000,
      });
    }

    setIsSubmitting(false);
  };

  return (
    <Card className="text-foreground mx-2 sm:mx-0">
      <CardContent className="p-3 sm:p-4 md:p-6">
        <Form {...form}>
          <form
            onSubmit={form.handleSubmit(onSubmit)}
            className="space-y-3 sm:space-y-4 contact-form"
          >
            <div className="grid grid-cols-1 md:grid-cols-2 gap-3 sm:gap-4 flex-safe">
              <FormField
                control={form.control}
                name="name"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-wrap-safe">
                      {t("contact.fields.name.label")}
                    </FormLabel>
                    <FormControl>
                      <Input
                        placeholder={t("contact.fields.name.placeholder")}
                        {...field}
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="email"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-wrap-safe">
                      {t("contact.fields.email.label")}
                    </FormLabel>
                    <FormControl>
                      <Input
                        type="email"
                        placeholder={t("contact.fields.email.placeholder")}
                        {...field}
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-3 sm:gap-4 flex-safe">
              <FormField
                control={form.control}
                name="phone"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-wrap-safe">
                      {t("contact.fields.phone.label")}
                    </FormLabel>
                    <FormControl>
                      <Input
                        placeholder={t("contact.fields.phone.placeholder")}
                        {...field}
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="company"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-wrap-safe">
                      {t("contact.fields.company.label")}
                    </FormLabel>
                    <FormControl>
                      <Input
                        placeholder={t("contact.fields.company.placeholder")}
                        {...field}
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-3 sm:gap-4 flex-safe">
              <FormField
                control={form.control}
                name="serviceType"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-wrap-safe">
                      {t("contact.fields.serviceType.label")}
                    </FormLabel>
                    <Select
                      onValueChange={field.onChange}
                      defaultValue={field.value}
                    >
                      <FormControl>
                        <SelectTrigger>
                          <SelectValue
                            placeholder={t(
                              "contact.fields.serviceType.placeholder"
                            )}
                          />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        {getServiceOptions(t).map((service) => (
                          <SelectItem key={service.value} value={service.value}>
                            {service.label}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="subject"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-wrap-safe">
                      {t("contact.fields.subject.label")}
                    </FormLabel>
                    <FormControl>
                      <Input
                        placeholder={t("contact.fields.subject.placeholder")}
                        {...field}
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <FormField
              control={form.control}
              name="message"
              render={({ field }) => (
                <FormItem>
                  <FormLabel className="text-wrap-safe">
                    {t("contact.fields.message.label")}
                  </FormLabel>
                  <FormControl>
                    <Textarea
                      placeholder={t("contact.fields.message.placeholder")}
                      className="min-h-[100px] resize-none"
                      {...field}
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="flex flex-col sm:flex-row gap-2 sm:gap-3 pt-3 sm:pt-4">
              <Button
                type="submit"
                disabled={isSubmitting}
                className="flex-1 bg-primary hover:bg-primary/90 text-primary-foreground"
              >
                {isSubmitting ? (
                  <>
                    <Loader2 className="me-2 h-4 w-4 animate-spin rtl-flip" />
                    {t("contact.buttons.sending")}
                  </>
                ) : (
                  <>
                    <Send className="me-2 h-4 w-4 rtl-flip" />
                    {t("contact.buttons.submit")}
                  </>
                )}
              </Button>

              <div className="flex gap-1 sm:gap-2">
                <Button
                  type="button"
                  variant="outline"
                  size="default"
                  asChild
                  className="flex-1 button-outline"
                >
                  <a
                    href="tel:+91-94627-71662"
                    className="flex items-center justify-center"
                  >
                    <Phone className="me-2 h-4 w-4 rtl-flip" />
                    {t("contact.buttons.callNow")}
                  </a>
                </Button>

                <Button
                  type="button"
                  variant="outline"
                  size="default"
                  asChild
                  className="flex-1 button-outline"
                >
                  <a
                    href="mailto:info@powertonengineering.com"
                    className="flex items-center justify-center"
                  >
                    <Mail className="me-2 h-4 w-4 rtl-flip" />
                    {t("contact.buttons.emailUs")}
                  </a>
                </Button>
              </div>
            </div>
          </form>
        </Form>
      </CardContent>
    </Card>
  );
}

client/src/utils/seo-enhancements.ts.content:
// Additional SEO enhancements for better search engine visibility

export interface BreadcrumbItem {
  name: string;
  url: string;
}

// Generate breadcrumb structured data
export function generateBreadcrumbData(items: BreadcrumbItem[]) {
  return {
    "@context": "https://schema.org",
    "@type": "BreadcrumbList",
    itemListElement: items.map((item, index) => ({
      "@type": "ListItem",
      position: index + 1,
      name: item.name,
      item: `https://powertonengineering.in${item.url}`,
    })),
  };
}

// Generate FAQ structured data
export function generateFAQData(
  faqs: Array<{ question: string; answer: string }>
) {
  return {
    "@context": "https://schema.org",
    "@type": "FAQPage",
    mainEntity: faqs.map((faq) => ({
      "@type": "Question",
      name: faq.question,
      acceptedAnswer: {
        "@type": "Answer",
        text: faq.answer,
      },
    })),
  };
}

// Generate enhanced product structured data
export function generateProductData(product: {
  name: string;
  description: string;
  image?: string;
  brand?: string;
  manufacturer?: string;
  category?: string;
  sku?: string;
  gtin?: string;
  mpn?: string;
  specifications?: Array<{ label: string; value: string }>;
  features?: string[];
  // Pricing information - optional but required for valid offers
  price?: number;
  lowPrice?: number;
  highPrice?: number;
  offerCount?: number;
  availability?: string;
}) {
  const productData: any = {
    "@context": "https://schema.org",
    "@type": "Product",
    name: product.name,
    description: product.description,
    brand: {
      "@type": "Brand",
      name: product.brand || "Powerton Engineering",
    },
    manufacturer: {
      "@type": "Organization",
      name: product.manufacturer || "Powerton Engineering Pvt. Ltd.",
      url: "https://powertonengineering.in",
    },
  };

  if (product.image) {
    productData.image = product.image;
  }

  if (product.category) {
    productData.category = product.category;
  }

  if (product.sku) {
    productData.sku = product.sku;
  }

  if (product.gtin) {
    productData.gtin = product.gtin;
  }

  if (product.mpn) {
    productData.mpn = product.mpn;
  }

  // Only add offers if we have pricing data
  // For single price products
  if (product.price !== undefined) {
    productData.offers = {
      "@type": "Offer",
      price: product.price.toString(),
      priceCurrency: "INR",
      availability: product.availability || "https://schema.org/InStock",
      seller: {
        "@type": "Organization",
        name: "Powerton Engineering Pvt. Ltd.",
      },
    };
  }
  // For price range products (multiple variants)
  else if (
    product.lowPrice !== undefined &&
    product.highPrice !== undefined &&
    product.offerCount !== undefined
  ) {
    productData.offers = {
      "@type": "AggregateOffer",
      lowPrice: product.lowPrice.toString(),
      highPrice: product.highPrice.toString(),
      priceCurrency: "INR",
      offerCount: product.offerCount,
      availability: product.availability || "https://schema.org/InStock",
      seller: {
        "@type": "Organization",
        name: "Powerton Engineering Pvt. Ltd.",
      },
    };
  }
  // If no pricing data provided, omit offers entirely (valid for B2B products)

  // Add additional specifications as properties
  if (product.specifications && product.specifications.length > 0) {
    productData.additionalProperty = product.specifications.map((spec) => ({
      "@type": "PropertyValue",
      name: spec.label,
      value: spec.value,
    }));
  }

  return productData;
}

// Generate product/service structured data
export function generateServiceData(
  services: Array<{
    name: string;
    description: string;
    url?: string;
    image?: string;
    serviceType?: string;
    areaServed?: string;
  }>
) {
  return {
    "@context": "https://schema.org",
    "@type": "ItemList",
    itemListElement: services.map((service, index) => ({
      "@type": "Service",
      position: index + 1,
      name: service.name,
      description: service.description,
      url: service.url
        ? `https://powertonengineering.in${service.url}`
        : undefined,
      image: service.image,
      serviceType: service.serviceType,
      areaServed: service.areaServed || "India",
      provider: {
        "@type": "Organization",
        name: "Powerton Engineering Pvt. Ltd.",
        url: "https://powertonengineering.in",
      },
    })),
  };
}

// Generate Article/BlogPosting structured data
export function generateArticleData(article: {
  title: string;
  description: string;
  datePublished: string;
  dateModified?: string;
  author?: string;
  image?: string;
  url: string;
}) {
  return {
    "@context": "https://schema.org",
    "@type": "Article",
    headline: article.title,
    description: article.description,
    datePublished: article.datePublished,
    dateModified: article.dateModified || article.datePublished,
    author: {
      "@type": "Organization",
      name: article.author || "Powerton Engineering Pvt. Ltd.",
    },
    publisher: {
      "@type": "Organization",
      name: "Powerton Engineering Pvt. Ltd.",
      logo: {
        "@type": "ImageObject",
        url: "https://powertonengineering.in/assets/mainlogopowerton (1)_1755674514195.png",
      },
    },
    image: article.image || "https://powertonengineering.in/og-image.jpg",
    mainEntityOfPage: {
      "@type": "WebPage",
      "@id": `https://powertonengineering.in${article.url}`,
    },
  };
}

// Generate local business hours
export function generateBusinessHoursData() {
  return {
    "@context": "https://schema.org",
    "@type": "LocalBusiness",
    openingHoursSpecification: [
      {
        "@type": "OpeningHoursSpecification",
        dayOfWeek: ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday"],
        opens: "09:00",
        closes: "18:00",
      },
      {
        "@type": "OpeningHoursSpecification",
        dayOfWeek: "Saturday",
        opens: "09:00",
        closes: "14:00",
      },
    ],
  };
}

// Meta tag helpers
export function generateMetaTags(page: {
  title: string;
  description: string;
  keywords?: string;
  ogImage?: string;
  canonicalUrl?: string;
}) {
  return {
    title: page.title,
    description: page.description,
    keywords:
      page.keywords ||
      "industrial automation, electrical engineering, control panels, India",
    ogImage: page.ogImage || "https://powertonengineering.in/og-image.jpg",
    canonicalUrl: page.canonicalUrl || "https://powertonengineering.in",
  };
}

